'use strict';
var test  = require('tape')
var color = require('../index')


test('simple color test', function(t){
  var a = color.moduleColor('foo')
  console.log('a is ', a)
  t.assert(a)
  var b = color.moduleColor('foo')
  var c = color.moduleColor('bar')
  t.assert(a === b, 'caching works')
  t.assert(b != c, 'colors are different')
  t.end()
})
