
var test = require('tape')
var dom = require('jsdom')

dom.env('../dom.html', {
  scripts: ["http://code.jquery.com/jquery.js", "./build.js"],
  done: function (errors, window) {
    var $ = window.jQuery
    console.log(window.document)

    test('svg exists in dom', function (t) {
      var $svg = $('body')
      t.ok($svg.length)
      t.end()
    })

  }
})


