'use strict';

var format = require('../')
var test = require('tape')

test('truncate', function (t) {
  var f = format.truncate
  var str = 'The quick brown fox jumped over the lazy dogs back.'
  t.equal(f(str, 4), 'The ...', 'Basic truncation')
  t.equal(f(str, 4, { seperator: '>>>' }), 'The >>>', 'Custom seperator')
  t.equal(f(str, 9, 6, { seperator: '...' }), 'The quick... back.', 'Truncate from front and back')
  t.equal(f(str, str.length - 5, 5), str, 'Return whole string if range is longer or equal to front+back ')
  t.end()
})
