var test = require('tape')

test('creation test', function(t){
  var d = require('../index.js')
  t.assert(d, 'require succeeded')
  t.assert(d.expressExample, 'Express Example exists')
  t.assert(d.simpleTest, 'SimpleTest exists')
  t.assert(d.express2, 'Express 2 exists')
  t.assert(d.expressExample2, 'Express Example2 exists')
  t.assert(d.expressExample3, 'Express Example3 exists')
  t.assert(d.glitchserver, 'Glitch Server example exists')
  t.assert(d.glitchserver.waterfalls.length, "Glitch Server has waterfalls")
  t.end()
})
