'use strict';
module.exports = function perfTest(cx){
  var test  = require('tape')
  var xform = require('../index')


  var data = require('strong-trace-waterfalldata')

  test('perf test for ghost data', function(t){
    var start, end, e, i
    cx.tracer.tag("enhanceWaterfall")
    for( i = 0; i < 10; i++ ){
      start = Date.now()
      e = xform.enhanceWaterfall(data.ghost)
      end = Date.now()
      console.log('elapsed time is ', end-start)
      t.assert(e)
    }
    t.end()
  })
}
